import React, { useState } from "react";
import { Center, Rows } from "../layouts/Line";
import { Box } from "../layouts/Box";
import "./Callback.css";

function Closures() {
  const textAreaView = (
    <div>
      <textarea
        id="w3review"
        name="w3review"
        rows="25"
        cols="56"
        defaultValue={"code"}
      />
    </div>
  );
  function outerFunction(outerVariable) {
    const outer2 = "outer2";
    return function innerFunction(innerVariable) {
      console.log("Outer Variable: " + outerVariable);
      console.log("Inner Variable: " + innerVariable);
    };
  }

  function handleSubmit(event) {
    event.preventDefault();
    const newFunction = outerFunction(event.target.outer.value);
    newFunction(event.target.inner.value);
  }

  return (
    <div>
      <Center>
        <Box>
          <h1>Closures</h1>
          <p>
            When you have a funtion defined inside another function that inner
            function has access to the vriales and scope even if the outer
            function finished executing and those variables no longer accessible
            outside that function.
          </p>
        </Box>
      </Center>
      <Center>
        <Rows>
          <Box>
            <form onSubmit={handleSubmit}>
              <label>
                Outer Variable:
                <input
                  type="text"
                  name="outer"
                  defaultValue={"I'm an outer value"}
                />
              </label>
              <br />
              <label>
                Inner Variable:
                <input
                  type="text"
                  name="inner"
                  defaultValue={"I'm an outer value"}
                />
              </label>
              <br />
              <input type="submit" value="Submit" />
            </form>
          </Box>
        </Rows>
      </Center>
      <Center>
        <Box>{textAreaView}</Box>
      </Center>
    </div>
  );
}

export default Closures;
