import React, { useState } from "react";
import { Center, Rows } from "../layouts/Line";
import { Box } from "../layouts/Box";

function Callback() {
  const [userLeft, setUserLeft] = useState(false);
  const [userWatchingCatMeme, setUserWatchingCatMeme] = useState(false);
  const [exampleView, setExampleView] = useState("Example View");
  const [message, setMessage] = useState("Welcome, User!");

  const handleUserLeftChange = () => {
    setUserLeft(!userLeft);
  };

  const handleUserWatchingCatMemeChange = () => {
    setUserWatchingCatMeme(!userWatchingCatMeme);
  };

  const textAreaView = (
    <div>
      <textarea
        id="w3review"
        name="w3review"
        rows="4"
        cols="50"
        defaultValue={exampleView}
      />
    </div>
  );

  function watchTutorialCallback(callback, errorCallback) {
    if (userLeft) {
      //   setTimeout(() => {
      errorCallback({
        name: "User Left",
        message: ":(",
      });
      //   }, 5000);
    } else if (userWatchingCatMeme) {
      //   setTimeout(() => {
      errorCallback({
        name: "User Watching Cat Meme",
        message: "WebDevSimplified < Cat",
      });
      //   }, 5000);
    } else {
      callback("Thumbs up and Subscribe");
    }
  }

  return (
    <div>
      <Center>
        <Rows>
          <h1>User Status</h1>
          <h3>
            {watchTutorialCallback(
              (the_message) => {
                //   setMessage("Success" + the_message);
                <div>{"Success" + the_message}</div>;
              },
              (error) => {
                <div>{error.name + ", " + error.message}</div>;
                // console.log(error.name + ", " + error.message);
              }
            )}
          </h3>
        </Rows>
      </Center>
      <Center>
        <Rows>
          <label>
            <input
              type="checkbox"
              checked={userLeft}
              onChange={handleUserLeftChange}
            />
            Did user leave?
          </label>

          <label>
            <input
              type="checkbox"
              checked={userWatchingCatMeme}
              onChange={handleUserWatchingCatMemeChange}
            />
            Is user watching a cat meme?
          </label>
        </Rows>
      </Center>
      <Center>
        <Box>{textAreaView}</Box>
      </Center>
    </div>
  );
}

export default Callback;
