import React, { useState } from "react";
import { Center, Rows } from "../layouts/Line";
import { Box } from "../layouts/Box";
import "./Callback.css";

function Closures() {
  const textAreaView = (
    <div>
      <textarea
        id="w3review"
        name="w3review"
        rows="25"
        cols="56"
        defaultValue={"code"}
      />
    </div>
  );
  function outerFunction(outerVariable) {
    return function innerFunction(innerVariable) {
      console.log("Outer Variable: " + outerVariable);
      console.log("Inner Variable: " + innerVariable);
    };
  }

  function handleSubmit(event) {
    event.preventDefault();
    console.log(event.target.outer.value);
    console.log(event.target.inner.value);
    const newFunction = outerFunction(event.target.outer.value);
    newFunction(event.target.inner.value);
  }

  return (
    <div>
      <Center>
        <Box>
          <h1>Closures</h1>
        </Box>
        <Box>
          <form>
            <label>
              Outer Variable:
              <input type="text" name="outer" />
            </label>
            <br />
            <label>
              Inner Variable:
              <input type="text" name="inner" />
            </label>
            <br />
            <input type="submit" value="Submit" onSubmit={handleSubmit()} />
          </form>
        </Box>
      </Center>
      <Center></Center>
      <Center>
        <Box>{textAreaView}</Box>
      </Center>
    </div>
  );
}

export default Closures;
